import React, { Component } from 'react';
import api from '../../services/api.js';
import './style.css';

export default class RegisterClient extends Component {

  constructor(props) {
    super(props);
    // {
    // 	"first_name":"tiago",
    // 	"last_name":"vilela",
    // 	"email":"mail@mail.com",
    // 	"phone_number":"9832658741",
    // 	"cellphone_number":"98998524712",
    // 	"birthdate":"2021/01/01",
    // 	"CPF":12345678,
    // 	"RG":"5555"
    // }
    this.state = {
        firstName: "",
        lastName: "",
        email: "",
        phoneNumber: "",
        cellphoneNumber: "",
        birthdate: "",
        cpf: "",
        rg: ""
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    // const target = event.target;
    const value = event.value;
    const name = event.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("state enviado -> " + this.state);
    this.RegisterClient();
  }

  RegisterClient = async () => {

    let headers = new Headers();
    headers.append('Content-Type', 'application/json');
    headers.append('Accept', 'application/json');
    headers.append('Access-Control-Allow-Origin', 'http://localhost:3000');
    headers.append('GET', 'POST', 'PUT', 'DELETE', 'OPTIONS');

    await api.post('/client', this.state, headers)
    .then(response => {
      alert("cliente cadastrado");
    })
    .catch(error => {
      alert("erro ao cadastrar cliente");
    });
  };

  render() {
    return (
      <div>
        <h2>Cadastro de Clientes</h2>

        <form onSubmit={this.handleSubmit}>

          <div>
          <label>Digite o seu Nome:
            <input name="firstName" type="text" value={this.state.firstName} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o seu sobrenome:
            <input name="lastName" type="text" value={this.state.lastName} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o e-mail:
            <input name="email" type="email" value={this.state.email} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o numero de telefone:
            <input name="phoneNumber" type="text" value={this.state.phoneNumber} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o numero celular:
            <input name="cellphoneNumber" type="text" value={this.state.cellphoneNumber} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite a sua data de nascimento:
            <input name="birthdate" type="date" value={this.state.birthdate} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o seu CPF:
            <input name="CPF" type="text" value={this.state.CPF} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o seu RG:
            <input name="RG" type="text" value={this.state.RG} onChange={this.handleInputChange}/>
          </label>
          </div>

          <input type="submit" value="Cadastrar" />

        </form>
      </div>
    );
  }
}
