import React, { Component } from 'react';
import api from '../../services/api.js';
// import { Link } from 'react-router-dom';

export default class ListClient extends Component {

  state = {
    client: [],
  };

  componentDidMount() {
    this.loadClient();
  }

  loadClient = async () => {
    const response = await api.get("/client");
    this.setState({client: response.data['client']});
  }

  render() {

    const { client } = this.state;

    return (
      <div>
        <h2>Lista de Clientes</h2>
        <p>Quantidade de clientes {client.length}</p>
        <div>
          {client.map(client => (
            <article key={client.id}>
              <p>
                Codigo: {client.id} <br />

                <strong>Nome:</strong> {client.firstName} {client.lastName}<br />
                <strong>E-mail:</strong> {client.email}<br />
                <strong>Telefone:</strong> {client.phoneNumber}<br />
                <strong>Celular:</strong> {client.cellphoneNumber}<br />
                <strong>Data de Nascimento:</strong> {client.birthDate}<br />
                <strong>CPF:</strong> {client.cpf}<br />
                <strong>RG:</strong> {client.rg}
              </p>
            </article>
          ))}
        </div>
      </div>
    );
  }
}
