import React, { Component } from 'react';
import api from '../../services/api';
import './style.css';

export default class RegisterClient extends Component {

  constructor(props) {
    // {
    // 	"first_name":"tiago",
    // 	"last_name":"vilela",
    // 	"email":"mail@mail.com",
    // 	"phone_number":"9832658741",
    // 	"cellphone_number":"98998524712",
    // 	"birthdate":"2021/01/01",
    // 	"CPF":12345678,
    // 	"RG":"5555"
    // }
    this.state = {
        first_name: "",
        lastName: "",
        phoneNumber: "",
        cellphoneNumber: "",
        birthDate: "",
        rg: ""
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    // const target = event.target;
    const value = event.value;
    const name = event.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("state enviado -> " + this.state);
    this.registerClient();
  }

  registerClient = async () => {

    let headers = new Headers();
    headers.append('Content-Type', 'application/json');
    headers.append('Accept', 'application/json');
    headers.append('Access-Control-Allow-Origin', 'http://localhost:3000');
    headers.append('GET', 'POST', 'PUT', 'DELETE', 'OPTIONS');

    await api.post('/client', this.state, headers)
    .then(response => {
      alert("cliente cadastrado");
    })
    .catch(error => {
      alert("erro ao cadastrar cliente");
    });
  };

  render() {
    return (
      <div>
        <h2>Cadastro de Clientes</h2>

        <form onSubmit={this.handleSubmit}>

          <div>
          <label>Digite o seu Nome:</label>
          <input name="first_name" type="text" value={this.state.first_name} onChange={this.handleInputChange}/>
          </div>

          <div>
          <label>Digite o seu sobrenome:
            <input name="last_name" type="text" value={this.state.lastName} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o numero de telefone:
            <input name="phone_number" type="text" value={this.state.phoneNumber} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o numero celular:
            <input name="cellphone_number" type="text" value={this.state.cellphoneNumber} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite a sua data de nascimento:
            <input name="birthdate" type="date" value={this.state.birthDate} onChange={this.handleInputChange}/>
          </label>
          </div>

          <div>
          <label>Digite o seu RG:
            <input name="RG" type="text" value={this.state.rg} onChange={this.handleInputChange}/>
          </label>
          </div>

          <input type="submit" value="Cadastrar" />

        </form>
      </div>
    );
  }
}
